glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
uniform glow {
  float dH;
  float dS;
  float dL;
};
void main () {
  vec4 o = vec4(1, 1, 1, 1);
  #if USE_TEXTURE
  vec4 texture_tmp = texture(texture, v_uv0);
  #if CC_USE_ALPHA_ATLAS_texture
      texture_tmp.a *= texture(texture, v_uv0 + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    o.rgb *= (texture_tmp.rgb * texture_tmp.rgb);
    o.a *= texture_tmp.a;
  #else
    o *= texture_tmp;
  #endif
  #endif
  o *= v_color;
  ALPHA_TEST(o);
  float r = o.r;
  float g = o.g;
  float b = o.b;
  float a = o.a;
  float h;
  float s;
  float l;
  {
    float max = r;
    if ( g > max ) max = g;
    if ( b > max ) max = b;
    float min = r;
    if ( g < min ) min = g;
    if ( b < min ) min = b;
    if ( max == min ) {
      h = 0.0;
    } else if ( max == r && g >= b ) {
      h = 60.0 * (g - b) / (max - min) + 0.0;
    } else if ( max == r && g < b ) {
      h = 60.0 * (g - b) / (max - min) + 360.0;
    } else if ( max == g ) {
      h = 60.0 * (b - r) / (max - min) + 120.0;
    } else if ( max == b ) {
      h = 60.0 * (r - g) / (max - min) + 240.0;
    }
    l = 0.5 * (max + min);
    if(l == 0.0 || max == min) {
      s = 0.0;
    } else if(0.0 <= l && l <= 0.5) {
      s = (max - min) / (2.0 * l);
    } else if(l > 0.5) {
      s = (max - min) / (2.0 - 2.0 * l);
    }
  }
  h = h + dH;
  s = s + dS;
  if ( s < 0.0 ){
    s = 0.0;
  }
  if ( s > 1.0 ) {
    s = 1.0;
  }
  l = l + dL;
  vec4 finalColor;
  {
    float q;
    if (l < 0.5) {
      q = l * (1.0 + s);
    } else if (l >= 0.5) {
      q = l + s - l * s;
    }
    float p = 2.0 * l - q;
    float hk = h / 360.0;
    float t[3];
    t[0] = hk + 1.0 / 3.0;
    t[1] = hk;
    t[2] = hk - 1.0 / 3.0;
    for (int i = 0; i < 3; i++) {
      if (t[i] < 0.0) t[i] += 1.0;
      if (t[i] > 1.0) t[i] -= 1.0;
    }
    float c[3];
    for (int i = 0; i < 3; i++) {
      if( t[i] < 1.0 / 6.0) {
        c[i] = p + ((q - p) * 6.0 * t[i]);
      } else if (1.0 / 6.0 <= t[i] && t[i] <0.5) {
        c[i] = q;
      } else if (0.5 <= t[i] && t[i] < 2.0 / 3.0) {
        c[i] = p + ((q - p) * 6.0 * (2.0 / 3.0 - t[i]));
      } else {
        c[i] = p;
      }
    }
    finalColor = vec4(c[0], c[1], c[2], a);
  }
  finalColor.r += dL;
  finalColor.g += dL;
  finalColor.b += dL;
  gl_FragColor = finalColor;
}

